import React,{useState} from 'react'

const App = () => {
     <h1>Like and dislike counter</h1>
    // eslint-disable-next-line no-undef
    const [count,setCount] = useState(0);

    const handleClick = () => {
        setCount(count+1);
    }
    const handleclick2 = () => {
        setCount(count-1);
    }
   return(
    <div>
  
     <h1>{count}</h1>
     <button onClick={handleClick}>+</button>
     <button onClick={handleclick2}>-</button>
    </div>
   )
}

export default App;
